fun isSubPath(head: ListNode?, root: TreeNode?): Boolean {
    node = head
    return isSubPathRecursive(head, root)
}

/**
 * 下面正确的算法应该是拿着头去每一个节点比一下，如果开头就不对就直接返回false
 * 我是先比一下当前传入的节点，如果是false再去比头节点，这样就造成了大量的重复
 * 因为如果正常的话，下面的节点还是会和头节点去比较的，而我又重复了这个过程
 * 时间复杂度是指数级的指数级了,  相当于我一个节点就去重复了一个树的遍历
 */
fun isSubPathRecursive(head: ListNode?, root: TreeNode?): Boolean {
    if (head == null) return true
    if (root == null) return false
    var left  = false
    var right = false
    if (head.`val` == root.`val`) {
        left = isSubPathRecursive(head.next, root.left)
        right = isSubPathRecursive(head.next, root.right)
    }
    if (left || right) return true

    if (node!!.`val` == root.`val`) {
        left = isSubPathRecursive(node!!.next, root.left)
        right = isSubPathRecursive(node!!.next, root.right)
    } else {
        left = isSubPathRecursive(node, root.left)
        right = isSubPathRecursive(node, root.right)
    }
    return left || right
}

/**
 * 从root开始深度优先搜索，然后不匹配就返回fasle
 * 每次去找自己和自己的叶子节点，都从head开始
 */
